import React, { useEffect } from "react";
import ReactDOM from "react-dom";
import axios from 'axios'
import TextField from '@material-ui/core/TextField';
import Autocomplete from '@material-ui/lab/Autocomplete';
import Grid from '@material-ui/core/Grid';
import CustomDebounce from '../utils/debounce'

function SearchBar() {

 const [searchTerm, setSearchTerm] = React.useState("");
 const [searchResults, setSearchResults] = React.useState([]);
 const handleChange = event => {
    setSearchTerm(event.target.value);
  };

const handleInputChange = (event) => {

    setSearchTerm(event.target.value)
}

const deboundSearch = CustomDebounce(searchTerm,300); //This will set delay to our search

console.log("Debounced search term  -",deboundSearch)

useEffect(() => {

    if (deboundSearch) {
        // Call countries API 
        searchCountries(deboundSearch).then((results)=>{
            // Set results in react state
            console.log("----->",results)
            setSearchResults(results);
        })
      } else {
        setSearchResults([]);
    }
  }, [deboundSearch]);


  const searchCountries = (code) => {

    return fetch(
        `http://localhost:5000/api/search`,
        {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({query:code}) 
        }
      )
        .then(r => r.json())
        .then(r => r.data.results)
        .catch(error => {
          console.error(error);
          return [];
        });

   } 

   return (
       <>
        <Autocomplete
        id="combo-box-demo"
        options={searchResults}

        getOptionLabel={(option) => option.title}
        style={{ width: 500 }}
        onInputChange={(e)=> handleChange(e)}
        renderInput={(params) => <TextField {...params} label="Countries" variant="outlined" />}
    />
    </>
   )
}

export default SearchBar
