import React, { useEffect } from "react";
import ReactDOM from "react-dom";
import axios from 'axios'
import TextField from '@material-ui/core/TextField';
import Autocomplete from '@material-ui/lab/Autocomplete';
import Grid from '@material-ui/core/Grid';
import customDebounce from '../utils/debounce'

function SearchBar() {

 const [searchTerm, setSearchTerm] = React.useState("");
 const [searchResults, setSearchResults] = React.useState([]);
 const handleChange = event => {
    setSearchTerm(event.target.value);
  };

const handleInputChange = (event) => {

    setSearchTerm(event.target.value)
}

const deboundSearch = customDebounce(searchTerm,300); //This will set delay to our search

useEffect(() => {
    axios.post(`http://localhost:5000/api/search`,{query:searchTerm})
      .then(({ data }) => {
          console.log("Response data -->",data)
         setSearchResults(data)
      })
  }, [searchTerm]);


   return (
       <>
        <Autocomplete
        id="combo-box-demo"
        options={searchResults}

        getOptionLabel={(option) => option.title}
        style={{ width: 500 }}
        onInputChange={(e)=> handleChange(e)}
        renderInput={(params) => <TextField {...params} label="Countries" variant="outlined" />}
    />
    </>
   )
}

export default SearchBar
